function loadJSON(e){const n=(navigator.language||navigator.userLanguage).replace("-","_"),t=["en","fr","es","de","pt","ja"].includes(n)?n:"en";fetch(`${t}.json`).then((e=>e.json())).then((n=>e(n))).catch((e=>console.error("Ошибка загрузки JSON: ",e)))}function updateTextElements(e){document.getElementById("Continue").innerHTML=e.Continue}loadJSON((function(e){updateTextElements(e)}));