function loadJSON(e){let n=new URLSearchParams(window.location.search).get("lang")||null;const t=["en","fr","es","de","pt","ja"];if(n)t.includes(n)||(n="en");else{const e=(navigator.language||navigator.userLanguage).replace("-","_");n=t.includes(e)?e:"en"}fetch(`${n}.json`).then((e=>e.json())).then((n=>e(n))).catch((e=>console.error("Ошибка загрузки JSON: ",e)))}function updateTextElements(e){document.getElementById("accessText").innerHTML=e["Get Unlimited <br>Access"],document.getElementById("creationText").innerHTML=e["Unlimited Art <br>Creation"],document.getElementById("stylesText").innerHTML=e["Exclusive <br>Styles"],document.getElementById("avatarsText").innerHTML=e["Magic Avatars <br>With 20% Off"],document.getElementById("yearlyAccessText").innerHTML=e["YEARLY ACCESS"],document.getElementById("bestOfferText").innerHTML=e["BEST OFFER"],document.getElementById("priceYearlyText").innerHTML=e["Just {{price}} per year"].replace("{{price}}","99.99"),document.getElementById("weeklyAccessText").innerHTML=e["WEEKLY ACCESS"],document.getElementById("priceWeeklyText").innerHTML=e["{{price}} <br>per week"].replace("{{price}}","2.99"),document.getElementById("Continue").innerHTML=e.Continue}loadJSON((function(e){updateTextElements(e)}));